        double x = 1 - 9.0 / 10;
        if (x == 0.1) {
            System.out.println("x is 0.1");
        } else {
            System.out.printf("x is not 0.1,x = %f\n",x);
        }
        // x not 0.1,x = 0.100000

        // 正确的方法是利用差值小于某个临界值来判断
        if(Math.abs(x-0.1)&lt;0.00001){
            System.out.println("x is 0.1");
        }else{
            System.out.println("x is not 0.1");
        }
        // x is 0.1

